// Background.js
import React from 'react';
import './Background.scss';

const Background = ({ children, backgroundColor, backgroundImageColor }) => {
  return (
    <div className="background-container" style={{ backgroundColor }}>
      <div className="background-images" style={{ filter: backgroundImageColor }}>
        {children}
        <iframe src="https://giphy.com/embed/l0HlCBfFfLqwS5AC4" width="480" height="480" frameBorder="0" class="giphy-embed" allowFullScreen></iframe>
      </div>
    </div>
  );
};

export default Background;
