// Background.js
import React from 'react';
import './Background.scss';

const Background = ({ children, leftBackgroundImage, rightBackgroundImage, mainBackgroundImage }) => {
  return (
    <div className="background-container">
      <div className="copyright">
        &copy; 2024 ZLBopti. Tous droits réservés.
      </div>
      <div className="background-images" style={{
        background: `
          url(${leftBackgroundImage}) center/cover no-repeat, 
          url(${rightBackgroundImage}) right/cover no-repeat, 
          url(${mainBackgroundImage}) center/cover no-repeat
        `
      }}>
        {children}
      </div>
    </div>
  );
};

export default Background;
