// Background.js
import React from 'react';
import './Background.scss';

const Background = ({ children, leftImage, rightImage, centerImage }) => {
  const backgroundStyles = {
    background: `
      url('${leftImage}') center/cover no-repeat,
      url('${rightImage}') right/cover no-repeat,
      url('${centerImage}') center/cover no-repeat
    `
  };

  return (
    <div className="background-container" style={backgroundStyles}>
      <div className="copyright">
        &copy; 2024 ZLBopti. Tous droits réservés.
      </div>
      <div className="background-images">
        {children}
      </div>
    </div>
  );
};

export default Background;
