// Background.js
import React from 'react';
import './Background.scss';

const Background = ({ children, backgroundColor }) => {
  const backgroundStyle = {
    background: `
      url('../../../src/images/backgroundLeft.png') center/cover no-repeat, 
      url('../../../src/images/backgroundRight.png') right/cover no-repeat, 
      url('../../../src/images/indexBackground.png') center/cover no-repeat,
      ${backgroundColor || 'transparent'}`
  };

  return (
    <div className="background-container" style={backgroundStyle}>
      <div className="background-images">
        {children}
      </div>
    </div>
  );
};

export default Background;
