// Background.js
import React from 'react';
import './Background.scss';

const Background = ({ children, backgroundImageColor }) => {
  return (
    <div className="background-container">
      <div className="background-images" style={{ filter: backgroundImageColor }}>
        {/* Ajout des images backgroundLeft.png et backgroundRight.png */}
        <div className="background-left" />
        <div className="background-right" />
        {children}
      </div>
    </div>
  );
};

export default Background;
