import React from 'react';
import './Background.scss';
import backgroundLeft from '../../../public/images/backgroundLeft.png';
import backgroundRight from '../../../public/images/backgroundRight.png';
import indexBackground from '../../../public/images/indexBackground.png';

const Background = ({ children }) => {
  return (
    <div className="background-container">
      <div className="background-images" style={{ backgroundImage: 
        `url(${backgroundLeft}) left/cover no-repeat, 
        url(${backgroundRight}) right/cover no-repeat, 
        url(${indexBackground}) center/cover no-repeat` }}>
        {children}
      </div>
    </div>
  );
};

export default Background;
