import React from 'react';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import Background from '../../components/background/Background';
import './Home.scss';

const Home = () => {
  // Change the background color according to the page
  const backgroundImageColor = 'brightness(1.2) sepia(0.5) hue-rotate(90deg)'; // Or any other color you want

  return (
    <Background backgroundColor="yourDesiredBackgroundColor" backgroundImageColor={backgroundImageColor}>
      <Header />
      <h1>Home</h1>
      {/* Ajoutez le contenu spécifique à la page ici */}
      <Footer />
    </Background>
  );
};

export default Home;
