// about.js
import React, { useState } from 'react';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import Background from '../../components/background/Background';
import Modal from '../../components/modal/Modal'; 
import './About.scss';

const About = () => {
  const [isModalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const handlePlayButtonClick = () => {
    // Ajoutez le code pour gérer le clic du bouton ici
    openModal();
    // Ajoutez le code pour ajouter ou retirer la classe 'toggled' à l'élément <i> ici
    const playButton = document.querySelector('.button-play-about i[anim="sheen"]');
    playButton.classList.toggle('toggled');
  };


  return (
    <Background pageClassName="background-images-about">
      <Header />
      <h1>about</h1>
      <div className='leftContent-about'>
        <div className='leftContentIcon-about'>
          <i className="fa-solid fa-bolt"></i>
          <i className="fa-solid fa-bug-slash"></i>
          <i className="fa-solid fa-heart"></i>
          <i className="fa-solid fa-hand"></i>
          <i className="fa-solid fa-gamepad"></i>
        </div>
        {!isModalOpen && (
          <div className='button-play-about' onClick={handlePlayButtonClick}>
  <i className="fa-solid fa-circle-play" anim="sheen"></i>
</div>

        )}
      </div>
      <Footer />

      {/* Modal component */}
      <Modal isOpen={isModalOpen} onClose={closeModal} pageClassName="close-button-about">
        {/* Content inside the modal */}
        <iframe
          title="YouTube video player"
          width="560"
          height="315"
          src="https://www.youtube.com/embed/zwL-r78BEdg"
          frameBorder="0"
          allowFullScreen
        ></iframe>
      </Modal>
    </Background>
  );
};

export default About;
