import React from 'react';
import './Modal.scss'; 

const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) {
    return null;
  }

  return (
    <div className="modal-overlay">
        <span className="close-button" onClick={onClose}>
          &times;
        </span>
        <div className="modal-content">
        {children}
        </div>
    </div>
  );
};

export default Modal;
