// Background.js
import React from 'react';
import './Background.scss';

const Background = ({
  children,
  backgroundLeftColor,
  backgroundRightColor,
  indexBackgroundColor,
}) => {
  return (
    <div className="background-container">
      <div
        className="background-left"
        style={{
          background: `rgb(${backgroundLeftColor.r}, ${backgroundLeftColor.g}, ${backgroundLeftColor.b}) radial-gradient(circle, rgba(${backgroundLeftColor.r}, ${backgroundLeftColor.g}, ${backgroundLeftColor.b}, 1) 0%, rgba(${backgroundRightColor.r}, ${backgroundRightColor.g}, ${backgroundRightColor.b}, 1) 100%)`,
        }}
      ></div>
      <div
        className="background-right"
        style={{
          background: `rgb(${backgroundRightColor.r}, ${backgroundRightColor.g}, ${backgroundRightColor.b}) radial-gradient(circle, rgba(${backgroundLeftColor.r}, ${backgroundLeftColor.g}, ${backgroundLeftColor.b}, 1) 0%, rgba(${backgroundRightColor.r}, ${backgroundRightColor.g}, ${backgroundRightColor.b}, 1) 100%)`,
        }}
      ></div>
      <div
        className="index-background"
        style={{ filter: indexBackgroundColor }}
      >
        {children}
      </div>
    </div>
  );
};

export default Background;
