// Background.js
import React from 'react';
import './Background.scss';

const Background = ({ children, backgroundColor }) => {
  return (
    <div className="background-container" style={{ backgroundColor }}>
      <div className="background-images">
        {/* Appliquer la classe uniquement Ã  backgroundLeft.png */}
        <div className={`background-left ${backgroundColor === "blue" ? 'filter-left-image' : ''}`} />
        <div className="background-right" />
        <div className="background-index" />
        {children}
      </div>
    </div>
  );
};

export default Background;
