// Modal.js
import React from 'react';
import './Modal.scss';

const Modal = ({ isOpen, onClose, children, pageClassName }) => {
  const overlayClass = isOpen ? 'modal-overlay open' : 'modal-overlay';
  const contentClass = isOpen ? 'modal-content open' : 'modal-content';

  if (!isOpen) {
    return null;
  }

  return (
    <div className={overlayClass}>
      <span className={pageClassName} onClick={onClose}>
        <i className="fa-solid fa-circle-xmark"></i>
      </span>
      <div className={contentClass}>
        {children}
      </div>
    </div>
  );
};

export default Modal;
